$(document).ready(function () {
  $(".alert").fadeOut(15000);
  /**----- password show and hide function -----**/
  $(".show_password").on("click", function () {
    const self = $(this);
    if (self.hasClass("fa-eye")) {
      $("#" + self.data("id")).attr("type", "text");
      self.removeClass("fa-eye");
      self.addClass("fa-eye-slash");
    } else if (self.hasClass("fa-eye-slash")) {
      $("#" + self.data("id")).attr("type", "password");
      self.removeClass("fa-eye-slash");
      self.addClass("fa-eye");
    }
  });
  /**----- password show and hide function -----**/
});
